settings.css:
/* =========================================================
   CONFIG — tweak ONLY these values
   ========================================================= */
:root{
  --tile: 60px;           /* avatar square size */
  --gap-x: 50px;          /* horizontal gap between tiles */
  --center-gap: 300px;    /* reserved middle gap (even count only) */
  --pad-bottom: 0px;      /* space below the entire row */

  --align-y: flex-end;    /* flex-start | center | flex-end */

  --name-h: 16px;         /* height of name label strip */
  --name-size: 12px;      /* font size for names */
  --name-pad-x: 6px;      /* horizontal padding inside name strip */
}

/* =========================================================
   1) OUTER CONTAINER — alignment & padding
   ========================================================= */
#embed .flex.h-screen{
  align-items: var(--align-y) !important;
  padding-bottom: var(--pad-bottom) !important;
}

/* =========================================================
   2) ROW CONTAINER — single horizontal row, no wrapping
   ========================================================= */
#embed .flex[style*="flex-direction: row"]{
  flex-wrap: nowrap !important;
  column-gap: var(--gap-x) !important; /* horizontal only */
  row-gap: 0 !important;
  align-items: var(--align-y) !important;
}

/* Kill responsive aspect-ratio wrappers */
#embed [style*="aspect-ratio"]{
  aspect-ratio: auto !important;
}

/* =========================================================
   3) FIXED-SIZE PARTICIPANT TILES
   ========================================================= */
#embed [data-discord-id]{
  flex: 0 0 auto !important;
  width: var(--tile) !important;
  height: calc(var(--tile) + var(--name-h)) !important;
  position: relative !important;
  overflow: hidden !important;
}

/* Inner relative wrapper */
#embed [data-discord-id] > .relative{
  width: var(--tile) !important;
  height: var(--tile) !important;
}

/* Avatar canvas */
#embed [data-discord-id] canvas{
  width: var(--tile) !important;
  height: var(--tile) !important;
  display: block !important;
}

/* =========================================================
   4) NAME LABEL — locked to bottom, no chaos
   ========================================================= */
#embed [data-discord-id] .absolute.break-all{
  left: 0 !important;
  right: 0 !important;
  bottom: 0 !important;

  height: var(--name-h) !important;
  line-height: var(--name-h) !important;
  font-size: var(--name-size) !important;

  padding: 0 var(--name-pad-x) !important;
  width: 100% !important;

  white-space: nowrap !important;
  overflow: hidden !important;
  text-overflow: ellipsis !important;

  word-break: normal !important;
  overflow-wrap: normal !important;

  text-align: center !important;
  z-index: 999 !important;
}

/* =========================================================
   5) MAGIC: CENTER GAP ONLY WHEN USER COUNT IS EVEN
   ========================================================= */

/* If total participant count is EVEN... */
#embed .flex[style*="flex-direction: row"]
:has([data-discord-id]:nth-child(even):last-child)
[data-discord-id]:nth-last-child(2){
  margin-right: var(--center-gap) !important;
}
